import React from "react"
import { Typography, Card, CardContent, CardActions } from "@material-ui/core"
import { useMutation, gql, useQuery, useSubscription } from "@apollo/client"
const DELETE_NOTES = gql`
  mutation deleteNote($id: Int!) {
    delete_notes_notes_by_pk(id: $id) {
      id
    }
  }
`
const NoteCard = (note) => {
  const [deleteNote] = useMutation(DELETE_NOTES)
  return (
    <Card>
      <CardContent>
        <Typography variant="body2" color="textSecondary" component="p">
          {note?.note?.content}
        </Typography>
      </CardContent>
      <CardActions
        onClick={() =>
          deleteNote({
            variables: {
              id: note?.note?.id,
            },
          })
        }
      >
        Delete
      </CardActions>
    </Card>
  )
}

export default NoteCard
